var parseBuffer = function (encoding) {
    var encoding = encoding || '1';
    var data = '';
    switch (encoding) {
        case '1':
            data = '1';
            break;
        case '2':
            zlib.gunzip(buf, function (err, decoded) {
                data = decoded && decoded.toString();
            });
            break;
        case '3':
            zlib.inflate(buf, function (err, decoded) {
                data = decoded && decoded.toString()
            });
            break;
        default:
            data = buf.toString();
    }
    return data;
};

var sort_list = function(seq){
    var l = seq.length;
    if (l >= 2){
        for (var i = 0; i<= l;i++ ){
            for (var j = i + 1; j<= l -1 ;j++ ){
                if (seq[i] > seq [j]){
                    tmp = seq[i];
                    seq[i] = seq[j];
                    seq[j] = tmp;
                    // seq[i], seq[j] = seq[j], seq[i];
                }
            }
        }
    }
    return seq
};

console.log(sort_list([1, 3, 4, 7, 6, 5, 2, 0]));